/*Table structure for table `xs_g2d1_server` */

DROP TABLE IF EXISTS `xs_g2d1_server`;

CREATE TABLE `xs_g2d1_server` (
  `id` char(21) CHARACTER SET ascii COLLATE ascii_bin NOT NULL COMMENT '服务商id，他处使用缩写srvid',
  `admin` char(21) CHARACTER SET ascii COLLATE ascii_bin NOT NULL COMMENT '管理员uid（一个人可以有多个）',
  `rrole` char(21) CHARACTER SET ascii COLLATE ascii_bin NOT NULL COMMENT '服务商管理根角色id',
  `nrole` char(21) CHARACTER SET ascii COLLATE ascii_bin NOT NULL COMMENT '普通角色id（添加的用户默认具有该角色）',
  `brole` char(21) CHARACTER SET ascii COLLATE ascii_bin NOT NULL COMMENT '引用桥梁根角色',
  `enableg2` char(1) CHARACTER SET ascii COLLATE ascii_bin NOT NULL DEFAULT '1' COMMENT '是否允许登陆公共后台：0否，1是',
  `logo` varchar(256) CHARACTER SET ascii COLLATE ascii_bin DEFAULT NULL,
  `name` varchar(32) COLLATE utf8mb4_bin NOT NULL COMMENT '服务商名称，唯一',
  `selfuser` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否只允许服务商自己添加（通过api创建）的用户登陆',
  `description` varchar(512) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '服务商描述',
  `createtime` datetime NOT NULL,
  `updatetime` datetime NOT NULL,
  `creator` char(21) COLLATE utf8mb4_bin NOT NULL,
  `rcode` varchar(256) COLLATE utf8mb4_bin NOT NULL,
  `hasguid` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否可以获取guid',
  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`),
  UNIQUE KEY `rid` (`rrole`),
  KEY `admin` (`admin`),
  KEY `nrole` (`nrole`),
  KEY `brole` (`brole`),
  CONSTRAINT `xs_g2d1_server_ibfk_1` FOREIGN KEY (`admin`) REFERENCES `xs_g2d1_user` (`id`),
  CONSTRAINT `xs_g2d1_server_ibfk_2` FOREIGN KEY (`rrole`) REFERENCES `xs_g2d1_role` (`id`),
  CONSTRAINT `xs_g2d1_server_ibfk_3` FOREIGN KEY (`nrole`) REFERENCES `xs_g2d1_role` (`id`),
  CONSTRAINT `xs_g2d1_server_ibfk_4` FOREIGN KEY (`brole`) REFERENCES `xs_g2d1_role` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin COMMENT='服务商';

