DROP TABLE IF EXISTS `xs_g2d1_user`;

CREATE TABLE `xs_g2d1_user` (
  `id` char(21) CHARACTER SET ascii COLLATE ascii_bin NOT NULL,
  `srvid` char(21) CHARACTER SET ascii COLLATE ascii_bin DEFAULT NULL COMMENT '服务商id,该服务商管理员或api可以修改此用户的密码的敏感信息',
  `luid` char(21) CHARACTER SET ascii COLLATE ascii_bin NOT NULL COMMENT '三方关联用户id',
  `name` varchar(32) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '用户昵称',
  `head` varchar(256) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '用户头像',
  `sex` char(1) CHARACTER SET ascii COLLATE ascii_bin DEFAULT NULL COMMENT '性别：0未设置，1男，2女',
  `login` varchar(20) COLLATE utf8mb4_bin DEFAULT NULL COMMENT '登陆名',
  `psw` varchar(128) CHARACTER SET ascii COLLATE ascii_bin DEFAULT NULL COMMENT '口令与盐的hash值',
  `salt` varchar(128) CHARACTER SET ascii COLLATE ascii_bin DEFAULT NULL COMMENT '口令盐',
  `remark` varchar(512) COLLATE utf8mb4_bin DEFAULT NULL,
  `createtime` datetime NOT NULL,
  `updatetime` datetime NOT NULL,
  `creator` char(21) CHARACTER SET ascii COLLATE ascii_bin DEFAULT NULL,
  `rcode` varchar(256) CHARACTER SET ascii COLLATE ascii_bin DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `luid` (`luid`),
  KEY `srvid` (`srvid`),
  CONSTRAINT `xs_g2d1_user_ibfk_1` FOREIGN KEY (`luid`) REFERENCES `xs_g2d1_thirduserlink` (`id`),
  CONSTRAINT `xs_g2d1_user_ibfk_2` FOREIGN KEY (`srvid`) REFERENCES `xs_g2d1_server` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin COMMENT='公共用户';

